BreathingState breathingState;


// countdown var
int countdownBreathing = 3;
unsigned long lastMillisCounter = 0;
unsigned long waitTime = 1000;
bool dirtyBegin = false;
bool dirtyCountdownSec = false;

// sim var 
unsigned long boxBreathingStart = 0;
unsigned long boxBreathingElapsed = 0;
int lastSecBox = -1;
bool horizontal, vertical = false;
bool dirtyMode, dirtyBox = false;
