int h = 88, k = 84; 
int last_x = 88, last_y = 84;
String mode = "BREATHE IN";
bool staticBox = false;
int32_t increment = 0.8;
bool invalidScreen = true;

void updateBoxBreathing(){
    if(digitalRead(BUTTON_START)==!HIGH){
        if(breathingState == MENU_STATE){
            countdownBreathing = 3;
            x_pos = 88, y_pos = 84;
            dirtyCountdownSec = true;
            breathingState = COUNTDOWN_STATE;
        }
    }
    if(digitalRead(BUTTON_STOP)==!HIGH){
        breathingState = MENU_STATE;
        staticBox = false;
    }

}

void drawBoxBreathing(){
    if(breathingState == MENU_STATE){
        drawMenuState();


    } else if(breathingState == COUNTDOWN_STATE){
       countdown();
    } else if(breathingState == SIMULATION_STATE){
        updateSim();
        drawSim();
    }

    img.unloadFont();
    img.pushSprite(0,0);
}

void drawMenuState(){
    img.fillSprite(TFT_BLACK);
    img.setTextSize(1);
    img.pushImage(10, 77, 16, 84, scroll_wheel);
    img.setFont(&fonts::Font4);
    img.setTextDatum(CL_DATUM);
    img.drawString("BOX", 42, 80);
    img.drawString("BREATHING", 42, 98);
    img.pushImage( 42, 143, 84, 24, defaultStart); 
}
void countdown(){
  unsigned long currentMs = millis();
  img.fillSprite(TFT_BLACK);
  img.setFont(&fonts::Font4);
  img.setTextDatum(CC_DATUM);
  img.setTextSize(3);

  if(countdownBreathing == 0) {
    
    if(currentMs - lastMillisCounter >= waitTime) {
      img.fillSprite(TFT_BLACK);
      tone(BUZZER, 1200, 500);
      img.drawString("BEGIN", 128, 128);
      breathingState = SIMULATION_STATE;
      lastMillisCounter = currentMs;
    }
    return;
  }
  
  if (currentMs - lastMillisCounter >= waitTime) {
        img.fillSprite(TFT_BLACK);
        tone(BUZZER, 1000, 500);
        img.drawString(String(countdownBreathing), 128, 128);
        
        countdownBreathing--;

        lastMillisCounter = currentMs;
      }
}

void updateSim(){
    boxBreathingElapsed = millis() - boxBreathingStart;

    int sec = boxBreathingElapsed / 1000;
    int stage = sec% 16;
    Serial.println(sec);
    Serial.println(stage);

    //init x pos = 88
    // init y pos = 84
    if(stage != lastSecBox){
        lastSecBox = stage;

        switch(stage){
            case 0:
                // dirtyMode = true;
                horizontal = true;
                vertical = false;
                mode = "BREATHE IN";
                // h = x_pos;
                increment = 0.8;
                break;
            case 4:
                // dirtyMode = true;
                horizontal = false;
                vertical = true;
                mode = "HOLD";
                // x_pos = h;
                // k = y_pos;
                increment = 0.8;
                break;
            case 8:
                // dirtyMode = true;
                horizontal = true;
                vertical = false;
                mode = "BREATHE OUT";
                // h = x_pos;
                // y_pos = k;
                increment = -0.8;
                break;
            case 12:
                // dirtyMode = true;
                horizontal = false;
                vertical = true;
                mode = "HOLD";
                // x_pos = h;
                // k = y_pos;
                increment = -0.8;
                break;
            case 16:    
                Serial.println("sim ended");
                increment = 0;
                breathingState = MENU_STATE;
                staticBox = false;
                return;

        }
        dirtyMode = true;
    }

    if(horizontal) h += increment;
    if(vertical) k += increment;

    x_pos = h;
    y_pos = k;

    dirtyBox = true;
}

void drawSim(){
    img.setTextSize(1);
    img.setTextDatum(CC_DATUM);
    img.setFont(&fonts::Font4);
    if(invalidScreen){
        img.fillSprite(TFT_BLACK);
    }
    

    img.drawRect(87, 83, 64, 64, TFT_WHITE);
    img.fillRect(56, 161, 128, 40, 0x4208);
    img.drawRect(57, 162, 126, 38, secondaryColor);
    img.pushImage(67, 169, 12, 12, heart);
    img.setTextFont(2);
    img.drawString("141", 73, 188);
    img.drawString("Press [STOP] to exit", 128, 209);
    staticBox = true;
    

    // if(!dirtyMode && !dirtyBox){
    //     img.fillRect(100, 64, 70, 35, TFT_CYAN);
    //     img.drawString(mode, 128, 66); 
    //     img.drawCircle(x_pos, y_pos, 4, TFT_RED);
    // }

    if(dirtyMode || invalidScreen) {
        img.fillRect(40, 40, 200, 40, TFT_BLACK);
        img.drawString(mode, 128, 66); 
        dirtyMode = false;
    }

    if(dirtyBox || invalidScreen) {
        img.fillCircle(last_x, last_y, 4, TFT_BLACK);

        if(last_x - 4 <= 87 || last_x + 4 >= 151 || last_y - 4 <= 83 || last_y + 4 >= 147){
            img.drawRect(87, 83, 64, 64, TFT_WHITE);
        }
        
        img.fillCircle(x_pos, y_pos, 4, TFT_RED);

        last_x = x_pos;
        last_y = y_pos;
        dirtyBox = false;
        invalidScreen = false;
    }
}
