void saveSteps(){
    EEPROM.put(0, steps);
    EEPROM.commit();
    Serial.printf("Steps saved to EEPROM: %d\n", steps);

}

void newDayReset(){
    for(int i = 0; i < 10; i++){
      EEPROM.write(i, 0);
    } 
    EEPROM.commit();
    steps = 0;
}
