    String display_m = "";
    static uint32_t lastUpdate = 0;
if(invalidateScreen) {
    img.fillSprite(TFT_BLACK);
  }
  img.setTextFont(2);
  img.setTextColor(TFT_WHITE);
  img.pushImage(144, 50, 50,50, full_battery_icon);
